import React, { useState, useEffect, useRef } from "react";
import styled from "styled-components";
import ArtworkList from "./components/ArtworkList";
import ArtworkDetail from "./components/ArtworkDetail";
import GlobalStyle from "./styles/GlobalStyle";
import client from "./mqttClient";

function App() {
  const [step, setStep] = useState("mode");
  const [selectedArtwork, setSelectedArtwork] = useState(null);
  const [floor, setFloor] = useState(1);          // ÌòÑÏû¨ ÏÇ¨ÎûåÏù¥ ÏûàÎäî Ï∏µ (Ï¥àÍ∏∞: 1Ï∏µ)
  const [arrived, setArrived] = useState(false);  // Î°úÎ¥á ÎèÑÏ∞© Ïó¨Î∂Ä (ÌëúÏãúÏö©)
  const subscribedOnce = useRef(false);           // Í∞úÎ∞úÎ™®Îìú Ï§ëÎ≥µ useEffect Í∞ÄÎìú

  // ‚úÖ MQTT payload ‚Üí boolean ÌååÏÑú (1Î∞îÏù¥Ìä∏ 0/1, Î¨∏ÏûêÏó¥ "true"/"false", JSONÍπåÏßÄ ÎåÄÏùë)
  const parseBoolPayload = (payload) => {
    if (payload && typeof payload.length === "number") {
      if (payload.length === 1 && (payload[0] === 0 || payload[0] === 1)) {
        return payload[0] === 1;
      }
    }
    try {
      const txt = new TextDecoder().decode(payload).trim().toLowerCase();
      if (txt === "true" || txt === "1") return true;
      if (txt === "false" || txt === "0") return false;
      const obj = JSON.parse(txt);
      if (typeof obj === "boolean") return obj;
      if (obj && typeof obj.arrived === "boolean") return obj.arrived;
      if (obj && typeof obj.detected === "boolean") return obj.detected;
    } catch (_) {}
    return false;
  };

  // ‚úÖ /robot3/arrived (ÌëúÏãúÏö©), /robot2/artwork_detected (ÌôîÎ©¥Ï†ÑÌôò Ìä∏Î¶¨Í±∞) Íµ¨ÎèÖ Î∞è Ï≤òÎ¶¨
  useEffect(() => {
    if (subscribedOnce.current) return;
    subscribedOnce.current = true;

    // Îëê ÌÜ†ÌîΩ Íµ¨ÎèÖ
    client.subscribe("/robot3/arrived", (err) => {
      if (err) console.error("‚ùå /robot3/arrived Íµ¨ÎèÖ Ïã§Ìå®", err);
      else console.log("üì° /robot3/arrived Íµ¨ÎèÖ ÏÑ±Í≥µ");
    });
    client.subscribe("/robot2/artwork_detected", (err) => {
      if (err) console.error("‚ùå /robot2/artwork_detected Íµ¨ÎèÖ Ïã§Ìå®", err);
      else console.log("üì° /robot2/artwork_detected Íµ¨ÎèÖ ÏÑ±Í≥µ");
    });

    const onMessage = (topic, payload) => {
      if (topic === "/robot3/arrived") {
        const isArrived = parseBoolPayload(payload);
        console.log("üì• ÏàòÏã†: /robot3/arrived =", isArrived);
        setArrived(isArrived); // ÌëúÏãúÎßå
        return;
      }

      if (topic === "/robot2/artwork_detected") {
        const detected = parseBoolPayload(payload);
        console.log("üì• ÏàòÏã†: /robot2/artwork_detected =", detected);
        // ‚úÖ ÏûëÌíà Ïù∏ÏãùÏù¥ trueÏùº ÎïåÎßå ÏÉÅÏÑ∏ ÌôîÎ©¥ÏúºÎ°ú Ï†ÑÌôò
        if (detected && selectedArtwork) {
          setStep("detail");
        }
        return;
      }
    };

    client.on("message", onMessage);
    return () => client.off("message", onMessage);
  }, [selectedArtwork]);

  const handleModeSelect = (mode) => {
    if (mode === "ÎßûÏ∂§ Í¥ÄÎûå") setStep("list");
    else alert("Ï†ÑÏ≤¥ Í¥ÄÎûå Î™®ÎìúÎäî ÌòÑÏû¨ Ï§ÄÎπÑ Ï§ëÏûÖÎãàÎã§.");
  };

  // ‚úÖ Ï∏µ Î≥ÄÍ≤ΩÏãú /floor_changed = 1Î∞îÏù¥Ìä∏ Î∂àÎ¶∞(0x01/0x00) Î∞úÌñâ
  const publishFloorChangedFlag = (changed) => {
    const buf = Uint8Array.from([changed ? 1 : 0]); // 1=true, 0=false
    client.publish("/floor_changed", buf);
    console.log("üì§ MQTT Î∞úÌñâ: /floor_changed ‚Üí", changed ? "<0x01>" : "<0x00>");
  };

  const handleSelect = (art) => {
    // ÏÉà Ïù¥Îèô ÏãúÏûë Ïãú ÏÉÅÌÉú Ï¥àÍ∏∞Ìôî
    setArrived(false);
    setSelectedArtwork(art);  // Î¨¥ÏóáÏùÑ Î≥¥Ïó¨Ï§ÑÏßÄ Í∏∞ÏñµÎßå Ìï¥ÎëêÍ≥†, ÌôîÎ©¥ Ï†ÑÌôòÏùÄ 'Ïù∏Ïãù' Ïã†Ìò∏ÏóêÏÑú

    // ÏûëÌíàÏù¥ ÏûàÎäî Ï∏µÍ≥º ÌòÑÏû¨ ÏÇ¨ÎûåÏù¥ ÏûàÎäî Ï∏µ ÎπÑÍµê
    if (art.floor !== floor) {
      if (window.confirm(`${art.floor}Ï∏µÏúºÎ°ú Ïù¥ÎèôÌï†ÍπåÏöî?`)) {
        publishFloorChangedFlag(true);
        setFloor(art.floor);
      } else {
        return; // Ï∑®ÏÜå Ïãú ÏïÑÎ¨¥ Í≤ÉÎèÑ Ïïà Ìï®
      }
    }

    // ÏûëÌíà IDÎ•º Îëê Î°úÎ¥áÏóê Ï†ÑÏÜ°
    const message = String(art.id);
    client.publish("/robot3/artwork_id", message);
    client.publish("/robot2/artwork_id", message);
    console.log("üì§ MQTT Î∞úÌñâ: /robot3/artwork_id ‚Üí", message);
    console.log("üì§ MQTT Î∞úÌñâ: /robot2/artwork_id ‚Üí", message);

    // ‚ùå Ïó¨Í∏∞ÏÑúÎäî setStep('detail') ÌïòÏßÄ ÏïäÏùå
    // ‚Ü≥ /robot2/artwork_detected == trueÍ∞Ä ÏàòÏã†ÎêòÎ©¥ ÏúÑ onMessageÏóêÏÑú detailÎ°ú Ï†ÑÌôò
  };

  const handleDone = () => setStep("more");

  return (
    <AppContainer>
      <GlobalStyle />
      <Layout>
        <Title>ORBIT</Title>

        {step === "mode" && (
          <>
            <Subtitle>Í¥ÄÎûå Î∞©Î≤ïÏùÑ ÏÑ†ÌÉùÌï¥ Ï£ºÏÑ∏Ïöî</Subtitle>
            <ModeSelectContainer>
              <Button onClick={() => handleModeSelect("Ï†ÑÏ≤¥ Í¥ÄÎûå")}>Ï†ÑÏ≤¥ Í¥ÄÎûå</Button>
              <Button onClick={() => handleModeSelect("ÎßûÏ∂§ Í¥ÄÎûå")}>ÎßûÏ∂§ Í¥ÄÎûå</Button>
            </ModeSelectContainer>
          </>
        )}

        {step === "list" && (
          <>
            <ArtworkList onSelect={handleSelect} floor={floor} />
            {/* ÌïÑÏöîÌïòÎ©¥ Ïó¨Í∏∞ÏÑú arrived ÏÉÅÌÉúÎ°ú "Ïù¥ÎèôÏ§ë/ÎèÑÏ∞©" ÏïàÎÇ¥ ÌëúÏãú Í∞ÄÎä• */}
            {/* <p>{arrived ? "ÎèÑÏ∞©ÌñàÏäµÎãàÎã§." : "Ïù¥Îèô Ï§ë..."}</p> */}
          </>
        )}

        {step === "detail" && selectedArtwork && (
          <ArtworkDetail
            artwork={selectedArtwork}
            onDone={handleDone}
            arrived={arrived}   // ÏÉÅÏÑ∏ÏóêÏÑú ÎèÑÏ∞© ÏÉÅÌÉú ÌôúÏö©ÌïòÍ≥† Ïã∂ÏúºÎ©¥ ÏÇ¨Ïö©
          />
        )}
      </Layout>
    </AppContainer>
  );
}

export default App;

// (Ïä§ÌÉÄÏùºÏùÄ Í∏∞Ï°¥ Í∑∏ÎåÄÎ°ú)

const AppContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background-color: #f8f8f8;
  display: flex;
  justify-content: center;
  align-items: flex-start;
  padding-top: 15vh;
`;

const Layout = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
`;

const Title = styled.h1`
  font-size: 5rem;
  color: #222;
  margin-bottom: 8vh; 
  letter-spacing: 0.1rem; 
`;

const Subtitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 6vh;
`;

const ModeSelectContainer = styled.div`
  display: flex;
  gap: 10rem;
  margin-top: 3vh; 

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
  }
`;

const Button = styled.button`
  font-size: 2rem;
  padding: 2rem 3.5rem;  
  border-radius: 16px;   
  border: none;
  background-color: #333;
  color: white;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #555;
  }

  &:active {
    background-color: #111;
  }
`;
