import React from "react";
import styled from "styled-components";

function ArtworkDetail({ artwork, onDone, arrived }) {
  if (!artwork) return null;

  return (
    <Container>
      {!arrived ? (
        <StatusText>로봇이 작품 위치로 이동하고 있어요</StatusText>
      ) : (
        <Content>
          <ImageWrap>
            <Image
              src={require(`../images/picture${artwork.id}.jpg`)}
              alt={artwork.name}
            />
          </ImageWrap>
          <InfoWrap>
            <Title>{artwork.name}</Title>
            <Author>{artwork.author}</Author>
            <Description>{artwork.description}</Description>

            <MoreButton onClick={onDone}>더 둘러보시겠어요?</MoreButton>
          </InfoWrap>
        </Content>
      )}
    </Container>
  );
}

export default ArtworkDetail;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem;
`;

const StatusText = styled.p`
  font-size: 1.8rem;
  margin-top: 4rem;
  color: #333;
`;

const Image = styled.img`
  width: 260px;
  height: 340px;
  object-fit: cover;
  border-radius: 12px;
  margin-bottom: 1.5rem;
  box-shadow: 0 6px 12px rgba(0,0,0,0.2);
`;

const Title = styled.h2`
  font-size: 2rem;
  margin: 0.5rem 0;
`;

const Author = styled.p`
  font-size: 1.2rem;
  color: #666;
  margin-bottom: 1rem;
`;

const Description = styled.p`
  font-size: 1.1rem;
  text-align: center;
  max-width: 500px;
  line-height: 1.6;
  color: #444;
`;

const MoreButton = styled.button`
  margin-top: 3rem;
  font-size: 1.4rem;
  padding: 1rem 2rem;
  background-color: #444;
  color: white;
  border: none;
  border-radius: 10px;
  cursor: pointer;

  &:hover {
    background-color: #666;
  }
`;
