import React, { useState } from "react";
import styled from "styled-components";
import ArtworkList from "./components/ArtworkList";
import ArtworkDetail from "./components/ArtworkDetail";
import MoreButton from "./components/MoreButton";
import GlobalStyle from "./styles/GlobalStyle";

function App() {
  const [step, setStep] = useState("mode");  // mode, list, detail, more
  const [selectedArtwork, setSelectedArtwork] = useState(null);

  const handleModeSelect = (mode) => {
    if (mode === "맞춤 관람") {
      setStep("list");
    } else {
      alert("전체 관람은 아직 준비 중입니다.");
    }
  };

  const handleSelect = (art) => {
    setSelectedArtwork(art);
    setStep("detail");
  };

  const handleDone = () => {
    setStep("more");
  };

  const handleNext = () => {
    setStep("list");
    setSelectedArtwork(null);
  };

  return (
    <AppContainer>
      <GlobalStyle />
      <Title>ORBIT</Title>
      {step === "mode" && (
        <ModeSelectContainer>
          <Subtitle>관람 방법을 선택해 주세요</Subtitle>
          <Button onClick={() => handleModeSelect("전체 관람")}>전체 관람</Button>
          <Button onClick={() => handleModeSelect("맞춤 관람")}>맞춤 관람</Button>
        </ModeSelectContainer>
      )}
      {step === "list" && <ArtworkList onSelect={handleSelect} />}
      {step === "detail" && <ArtworkDetail artwork={selectedArtwork} onDone={handleDone} />}
      {step === "more" && <MoreButton onClick={handleNext} />}
    </AppContainer>
  );
}

export default App;

const AppContainer = styled.div`
  position: relative;
  width: 100vw;
  height: 100vh;
  background-color: #f8f8f8;
  overflow: hidden;
`;

const Title = styled.h1`
  position: absolute;
  top: 20%;
  left: 50%;
  transform: translate(-50%, -50%);
  font-size: 4rem;
  color: #222;
`;

const Subtitle = styled.h2`
  position: absolute;
  top: 30%;
  left: 50%;
  transform: translate(-50%, -50%);
  font-size: 2rem;
`;

const ModeSelectContainer = styled.div`
  position: absolute;

  left: 50%;
  transform: translate(-50%, -50%);
  display: flex;
  gap: 2rem;

  @media (max-width: 768px) {
    flex-direction: column;
    top: 55%;
  }
`;

const Button = styled.button`
  font-size: 2rem;
  padding: 1rem 2rem;
  border-radius: 12px;
  border: none;
  background-color: #333;
  color: white;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #555;
  }

  &:active {
    background-color: #111;
  }
`;
