import React, { useState } from "react";
import ArtworkList from "./components/ArtworkList";
import ArtworkDetail from "./components/ArtworkDetail";
import MoreButton from "./components/MoreButton";
import './App.css';

function App() {
  const [step, setStep] = useState("mode");  // mode, list, detail, more
  const [selectedArtwork, setSelectedArtwork] = useState(null);

  const handleModeSelect = (mode) => {
    if (mode === "맞춤 관람") {
      setStep("list");
    } else {
      alert("기존 관람은 아직 준비 중입니다.");
    }
  };

  const handleSelect = (art) => {
    setSelectedArtwork(art);
    setStep("detail");
  };

  const handleDone = () => {
    setStep("more");
  };

  const handleNext = () => {
    setStep("list");
    setSelectedArtwork(null);
  };

  return (
    <div className="App">
      <h1>ORBIT</h1>
      {step === "mode" && (
        <div>
          <h2>관람 방법을 선택해 주세요.</h2>
          <button onClick={() => handleModeSelect("전체 관람")}>전체 관람</button>
          <button onClick={() => handleModeSelect("맞춤 관람")}>맞춤 관람</button>
        </div>
      )}
      {step === "list" && <ArtworkList onSelect={handleSelect} />}
      {step === "detail" && <ArtworkDetail artwork={selectedArtwork} onDone={handleDone} />}
      {step === "more" && <MoreButton onClick={handleNext} />}
    </div>
  );
}

export default App;
