import React, { useState } from "react";
import styled from "styled-components";
import ArtworkList from "./components/ArtworkList";
import ArtworkDetail from "./components/ArtworkDetail";
import GlobalStyle from "./styles/GlobalStyle";
import client from "./mqttClient";

function App() {
  const [step, setStep] = useState("mode");
  const [selectedArtwork, setSelectedArtwork] = useState(null);
  const [currentFloor, setCurrentFloor] = useState(1); // 현재 사람의 층
  const [pendingArtwork, setPendingArtwork] = useState(null);
  const [showFloorConfirm, setShowFloorConfirm] = useState(false);

  const handleModeSelect = (mode) => {
    if (mode === "맞춤 관람") {
      setStep("list");
    } else {
      alert("전체 관람 모드는 현재 준비 중입니다.");
    }
  };

  const moveToArtwork = (art) => {
    const message = JSON.stringify({ id: art.id });
    client.publish("/request/artwork", message);
    console.log("📤 MQTT 발행: /request/artwork →", message);

    setSelectedArtwork(art);
    setStep("detail");
  };

  const handleSelect = (art) => {
    const artFloor = parseInt(art.floor); // 명확하게 숫자 비교
    if (artFloor !== currentFloor) {
      setPendingArtwork(art);
      setShowFloorConfirm(true);
    } else {
      moveToArtwork(art);
    }
  };

  const handleFloorConfirm = () => {
    if (pendingArtwork) {
      setCurrentFloor(parseInt(pendingArtwork.floor));
      moveToArtwork(pendingArtwork);
      setPendingArtwork(null);
      setShowFloorConfirm(false);
    }
  };

  const handleCancel = () => {
    setPendingArtwork(null);
    setShowFloorConfirm(false);
  };

  const handleDone = () => {
    setStep("more");
  };

  return (
    <AppContainer>
      <GlobalStyle />
      <Layout>
        <Title>ORBIT</Title>

        {step === "mode" && (
          <>
            <Subtitle>관람 방법을 선택해 주세요</Subtitle>
            <ModeSelectContainer>
              <Button onClick={() => handleModeSelect("전체 관람")}>전체 관람</Button>
              <Button onClick={() => handleModeSelect("맞춤 관람")}>맞춤 관람</Button>
            </ModeSelectContainer>
          </>
        )}

        {step === "list" && (
          <>
            <ArtworkList onSelect={handleSelect} />
            {showFloorConfirm && pendingArtwork && (
              <FloorAlert>
                <p>{pendingArtwork.floor}층에 있는 작품입니다. 이동하시겠습니까?</p>
                <div>
                  <AlertButton onClick={handleFloorConfirm}>네</AlertButton>
                  <AlertButton onClick={handleCancel}>아니오</AlertButton>
                </div>
              </FloorAlert>
            )}
          </>
        )}

        {step === "detail" && <ArtworkDetail artwork={selectedArtwork} onDone={handleDone} />}
      </Layout>
    </AppContainer>
  );
}

export default App;
const AppContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background-color: #f8f8f8;
  display: flex;
  justify-content: center;
  align-items: flex-start;
  padding-top: 15vh;
`;

const Layout = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
`;

const Title = styled.h1`
  font-size: 5rem;
  color: #222;
  margin-bottom: 8vh; 
  letter-spacing: 0.1rem; 
`;

const Subtitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 6vh;
`;

const ModeSelectContainer = styled.div`
  display: flex;
  gap: 10rem;
  margin-top: 3vh; 

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
  }
`;

const Button = styled.button`
  font-size: 2rem;
  padding: 2rem 3.5rem;  
  border-radius: 16px;   
  border: none;
  background-color: #333;
  color: white;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #555;
  }

  &:active {
    background-color: #111;
  }
`;

const FloorAlert = styled.div`
  background: white;
  border: 2px solid #444;
  padding: 2rem;
  border-radius: 12px;
  margin-top: 2rem;
  text-align: center;

  p {
    font-size: 1.5rem;
    margin-bottom: 1rem;
  }

  div {
    display: flex;
    justify-content: center;
    gap: 1.5rem;
  }
`;

const AlertButton = styled.button`
  font-size: 1.2rem;
  padding: 0.8rem 1.6rem;
  background-color: #333;
  color: white;
  border: none;
  border-radius: 8px;
  cursor: pointer;

  &:hover {
    background-color: #555;
  }
`;
