import React, { useEffect, useState, useCallback } from "react";
import useSocket from "../hooks/useSocket";
import styled from "styled-components";

function ArtworkDetail({ artwork, onDone }) {
  const [isMoving, setIsMoving] = useState(true);         // 이동 중 상태
  const [showInfo, setShowInfo] = useState(false);        // 작품 정보 표시 여부
  const [showButton, setShowButton] = useState(false);    // 버튼 표시 여부

  const handleMessage = useCallback((data) => {
    console.log("서버 응답:", data);

    // 이동 완료 신호 도착 시
    if (data.arrived) {
      setIsMoving(false);
      setShowInfo(true);

      // 5초 뒤에 버튼 보이기
      setTimeout(() => {
        setShowButton(true);
      }, 5000);
    }
  }, []);

  const { send } = useSocket(handleMessage);

  useEffect(() => {
    // 이동 명령 전송
    send({
      command: "go_to",
      artwork: artwork.name,
      x: artwork.x,
      y: artwork.y,
      z: artwork.z
    });
  }, [artwork, send]);

  return (
    <Container>
      {isMoving ? (
        <StatusText>로봇이 작품 위치로 이동하고 있어요</StatusText>
      ) : showInfo ? (
        <>
          <Image
            src={require(`../images/picture${artwork.id}.jpg`)}
            alt={artwork.name}
          />
          <Title>{artwork.name}</Title>
          <Author>{artwork.author}</Author>
          <Description>{artwork.description}</Description>

          {showButton && (
            <NextButton onClick={onDone}>더 둘러보시겠어요?</NextButton>
          )}
        </>
      ) : null}
    </Container>
  );
}

export default ArtworkDetail;

// ✅ Styled Components
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem;
`;

const StatusText = styled.p`
  font-size: 1.8rem;
  margin-top: 4rem;
  color: #333;
`;

const Image = styled.img`
  width: 260px;
  height: 340px;
  object-fit: cover;
  border-radius: 12px;
  margin-bottom: 1.5rem;
  box-shadow: 0 6px 12px rgba(0,0,0,0.2);
`;

const Title = styled.h2`
  font-size: 2rem;
  margin: 0.5rem 0;
`;

const Author = styled.p`
  font-size: 1.2rem;
  color: #666;
  margin-bottom: 1rem;
`;

const Description = styled.p`
  font-size: 1.1rem;
  text-align: center;
  max-width: 500px;
  line-height: 1.6;
  color: #444;
`;

const NextButton = styled.button`
  font-size: 1.2rem;
  margin-top: 2rem;
  `;
