import React, { useState, useEffect } from "react";
import styled from "styled-components";
import ArtworkList from "./components/ArtworkList";
import ArtworkDetail from "./components/ArtworkDetail";
import GlobalStyle from "./styles/GlobalStyle";
import client from "./mqttClient";

function App() {
  const [step, setStep] = useState("mode");  
  const [selectedArtwork, setSelectedArtwork] = useState(null);
  const [floor, setFloor] = useState(1);  // 초기 층수는 1층
  const [pendingArtwork, setPendingArtwork] = useState(null);

  useEffect(() => {
    client.on("message", (topic, message) => {
      if (topic === "/robot3/arrived") {
        console.log("✅ 도착 메시지 수신", message.toString());
        if (pendingArtwork) {
          setSelectedArtwork(pendingArtwork);
          setStep("detail");
          setPendingArtwork(null);
        }
      }
    });
  }, [pendingArtwork]);

  const handleModeSelect = (mode) => {
    if (mode === "맞춤 관람") {
      setStep("list");
    } else {
      alert("전체 관람 모드는 현재 준비 중입니다.");
    }
  };

  const handleSelect = (art) => {
    const message = JSON.stringify({ id: art.id });
    client.publish("/request/artwork", message);
    console.log("📤 MQTT 발행: /request/artwork →", message);

    if (art.floor !== floor) {
      if (window.confirm(`${art.floor}층으로 이동할까요?`)) {
        setFloor(art.floor);
        setPendingArtwork(art);  // 도착 확인 후 보여주기 위해 대기 상태
      }
    } else {
      setSelectedArtwork(art);
      setStep("detail");
    }
  };

  const handleDone = () => {
    setStep("more");
  };

  return (
    <AppContainer>
      <GlobalStyle />
      <Layout>
        <Title>ORBIT</Title>
        {step === "mode" && (
          <>
            <Subtitle>관람 방법을 선택해 주세요</Subtitle>
            <ModeSelectContainer>
              <Button onClick={() => handleModeSelect("전체 관람")}>전체 관람</Button>
              <Button onClick={() => handleModeSelect("맞춤 관람")}>맞춤 관람</Button>
            </ModeSelectContainer>
          </>
        )}
        {step === "list" && <ArtworkList onSelect={handleSelect} floor={floor} />}
        {step === "detail" && <ArtworkDetail artwork={selectedArtwork} onDone={handleDone} />}
      </Layout>
    </AppContainer>
  );
}

export default App;

const AppContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background-color: #f8f8f8;
  display: flex;
  justify-content: center;
  align-items: flex-start;
  padding-top: 15vh;
`;

const Layout = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
`;

const Title = styled.h1`
  font-size: 5rem;
  color: #222;
  margin-bottom: 8vh; 
  letter-spacing: 0.1rem; 
`;

const Subtitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 6vh;
`;

const ModeSelectContainer = styled.div`
  display: flex;
  gap: 10rem;
  margin-top: 3vh; 

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
  }
`;

const Button = styled.button`
  font-size: 2rem;
  padding: 2rem 3.5rem;  
  border-radius: 16px;   
  border: none;
  background-color: #333;
  color: white;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #555;
  }

  &:active {
    background-color: #111;
  }
`;
