// src/mqttClient.js
import mqtt from 'mqtt';

const options = {
  username: "parkjihye",
  password: "rokey1234",
};

const client = mqtt.connect("wss://xbe65600.ala.us-east-1.emqxsl.com:8084/mqtt", options);
// ※ 포트 8084는 EMQX의 WebSocket over TLS 기본값입니다.

client.on("connect", () => {
  console.log("✅ MQTT 연결 완료");
});

client.on("error", (err) => {
  console.error("❌ MQTT 연결 실패:", err);
});

export default client;
