import React, { useState, useEffect, useRef } from "react";
import styled from "styled-components";
import ArtworkList from "./components/ArtworkList";
import ArtworkDetail from "./components/ArtworkDetail";
import GlobalStyle from "./styles/GlobalStyle";
import client from "./mqttClient";

function App() {
  const [step, setStep] = useState("mode");                 // ÌòÑÏû¨ ÌôîÎ©¥ Îã®Í≥Ñ
  const [selectedArtwork, setSelectedArtwork] = useState(null); // ÏÑ†ÌÉùÎêú ÏûëÌíà
  const [floor, setFloor] = useState(1);                    // ÌòÑÏû¨ ÏÇ¨ÎûåÏù¥ ÏûàÎäî Ï∏µ
  const [personDetected, setPersonDetected] = useState(false); // ÏÇ¨Îûå Ïù∏Ïãù Ïó¨Î∂Ä
  const [detected, setDetected] = useState(false);          // /robot2/artwork_detected
  const [arrived, setArrived] = useState(false);            // üîπ /robot2|3/arrived
  const [gohomeSent, setGohomeSent] = useState(false);
  const [artworkDetectedFlag, setArtworkDetectedFlag] = useState(false);
  const [arrivedPaintingFlag, setArrivedPaintingFlag] = useState(false);


  // ÏµúÏã† ÏÑ†ÌÉùÏûëÌíà Ï∞∏Ï°∞Ïö© ref (Î©îÏãúÏßÄ Ìï∏Îì§Îü¨ÏóêÏÑú ÏµúÏã†Í∞í ÏÇ¨Ïö©)
  const selectedRef = useRef(null);
  useEffect(() => { selectedRef.current = selectedArtwork; }, [selectedArtwork]);

  // MQTT payload ‚Üí boolean ÌååÏÑú
  const parseBoolPayload = (payload) => {
    if (payload && typeof payload.length === "number") {
      if (payload.length === 1 && (payload[0] === 0 || payload[0] === 1)) {
        return payload[0] === 1;
      }
    }
    try {
      const txt = new TextDecoder().decode(payload).trim().toLowerCase();
      if (txt === "true" || txt === "1") return true;
      if (txt === "false" || txt === "0") return false;
      const obj = JSON.parse(txt);
      if (typeof obj === "boolean") return obj;
      if (obj && typeof obj.detected === "boolean") return obj.detected;
      if (obj && typeof obj.arrived === "boolean") return obj.arrived;
    } catch (_) {}
    return false;
  };

  // Íµ¨ÎèÖ: Ìïú Î≤àÎßå
  useEffect(() => {
    // üîπ ÎèÑÏ∞© ÌÜ†ÌîΩ Ìè¨Ìï®
    client.subscribe("/robot2/arrived", (err) =>
      err ? console.error("‚ùå /robot2/arrived Íµ¨ÎèÖ Ïã§Ìå®", err)
          : console.log("üì° /robot2/arrived Íµ¨ÎèÖ ÏÑ±Í≥µ")
    );
    client.subscribe("/robot3/arrived", (err) =>
      err ? console.error("‚ùå /robot3/arrived Íµ¨ÎèÖ Ïã§Ìå®", err)
          : console.log("üì° /robot3/arrived Íµ¨ÎèÖ ÏÑ±Í≥µ")
    );
    client.subscribe("/robot2/artwork_detected", (err) =>
      err ? console.error("‚ùå /robot2/artwork_detected Íµ¨ÎèÖ Ïã§Ìå®", err)
          : console.log("üì° /robot2/artwork_detected Íµ¨ÎèÖ ÏÑ±Í≥µ")
    );
    client.subscribe("/robot2/arrived_painting", (err) =>
      err ? console.error("‚ùå /robot2/arrived_painting Íµ¨ÎèÖ Ïã§Ìå®", err)
          : console.log("üì° /robot2/arrived_painting Íµ¨ÎèÖ ÏÑ±Í≥µ")
    );
    client.subscribe("/robot2/person_detected", (err) =>
      err ? console.error("‚ùå /robot2/person_detected Íµ¨ÎèÖ Ïã§Ìå®", err)
          : console.log("üì° /robot2/person_detected Íµ¨ÎèÖ ÏÑ±Í≥µ")
    );

    const onMessage = (topic, payload) => {
      // üîπ ÎèÑÏ∞© ÌÜ†ÌîΩ Ï≤òÎ¶¨
      if (topic === "/robot2/arrived" || topic === "/robot3/arrived") {
        const isArrived = parseBoolPayload(payload);
        console.log(`üì• ${topic} =`, isArrived);
        setArrived(isArrived);             // ÌôîÎ©¥ Ï†ÑÌôòÏùÄ Ïó¨Í∏∞ÏÑú ÌïòÏßÄ ÏïäÏùå
        return;
      }

      if (topic === "/robot2/artwork_detected") {
        const ok = parseBoolPayload(payload);
        console.log("üì• /robot2/artwork_detected =", ok);
        setDetected(ok);                    // ÏÉÅÌÉú Î∞òÏòÅ
        if (ok && selectedRef.current) {
          setStep("detail");                // üîπ Ïù∏ÏãùÎêòÎ©¥ ÏÉÅÏÑ∏ ÌôîÎ©¥ÏúºÎ°ú Ï†ÑÌôò
        }
        return;
      }

      if (topic === "/robot2/person_detected") {
        const v = parseBoolPayload(payload);
        console.log("üì• /robot2/person_detected =", v);
        setPersonDetected(v);

        if (v && !gohomeSent) {
          const one = Uint8Array.from([1]); // 1Î∞îÏù¥Ìä∏ Î∂àÎ¶∞
          client.publish("/robot3/gohome", one);
          console.log("üì§ /robot3/gohome ‚Üí <0x01>");
          setGohomeSent(true);
        }
        // (ÏÑ†ÌÉù) Ïù∏Ïãù Ìï¥Ï†úÎêòÎ©¥ ÌîåÎûòÍ∑∏ Î¶¨ÏÖãÌï¥ÏÑú Îã§ÏùåÏóê Îã§Ïãú Î≥¥ÎÇº Ïàò ÏûàÍ≤å Ìï®
        if (!v && gohomeSent) {
          setGohomeSent(false);
        }
        return;
      }
    };

    client.on("message", onMessage);
    return () => client.off("message", onMessage);
  }, []);

  const handleModeSelect = (mode) => {
    if (mode === "ÎßûÏ∂§ Í¥ÄÎûå") setStep("list");
    else alert("Ï†ÑÏ≤¥ Í¥ÄÎûå Î™®ÎìúÎäî ÌòÑÏû¨ Ï§ÄÎπÑ Ï§ëÏûÖÎãàÎã§.");
  };

  // Ï∏µ Î≥ÄÍ≤ΩÏãú /floor_changed = 1Î∞îÏù¥Ìä∏ Î∂àÎ¶∞(0x01/0x00) Î∞úÌñâ
  const publishFloorChangedFlag = (changed) => {
    const buf = Uint8Array.from([changed ? 1 : 0]); // 1=true, 0=false
    client.publish("/floor_changed", buf);
    console.log("üì§ /floor_changed ‚Üí", changed ? "<0x01>" : "<0x00>");
  };

  const handleSelect = (art) => {
    // ÏÉà ÏûëÌíà ÏÑ†ÌÉù Ïãú Ïù¥Ï†Ñ ÏÉÅÌÉú Ï¥àÍ∏∞Ìôî
    setDetected(false);
    setArrived(false); // üîπ ÏÉà Ïù¥Îèô ÏãúÏûë ‚Üí ÏïÑÏßÅ ÎèÑÏ∞© Ïïà Ìï®

    if (art.floor !== floor) {
      if (window.confirm(`${art.floor}Ï∏µÏúºÎ°ú Ïù¥ÎèôÌï†ÍπåÏöî?`)) {
        publishFloorChangedFlag(true);
        setFloor(art.floor);

        const message = String(art.id);
        client.publish("/robot3/artwork_id", message);
        client.publish("/robot2/artwork_id", message);
        console.log("üì§ /robot3/artwork_id ‚Üí", message);
        console.log("üì§ /robot2/artwork_id ‚Üí", message);

        // ÌôîÎ©¥ Ï†ÑÌôòÏùÄ MQTT Ïù∏Ïãù Ïã†Ìò∏Î•º Í∏∞Îã§Î¶º
        setSelectedArtwork(art);
        selectedRef.current = art;
        setStep("detail");
      }
    } else {
      const message = String(art.id);
      client.publish("/robot3/artwork_id", message);
      client.publish("/robot2/artwork_id", message);
      console.log("üì§ /robot3/artwork_id ‚Üí", message);
      console.log("üì§ /robot2/artwork_id ‚Üí", message);

      setSelectedArtwork(art); // Ï†ÑÌôòÏùÄ Ïù∏Ïãù Ïã†Ìò∏Í∞Ä Ïò§Î©¥ ÏàòÌñâ
      selectedRef.current = art;
      setStep("detail");
    }
  };

  const handleDone = () => setStep("more"); // ÌïÑÏöî Ïãú "list" Îì±ÏúºÎ°ú Î≥ÄÍ≤Ω

  return (
    <AppContainer>
      <GlobalStyle />
      <Layout>
        <Title>ORBIT</Title>

        {step === "mode" && (
          <>
            <Subtitle>Í¥ÄÎûå Î∞©Î≤ïÏùÑ ÏÑ†ÌÉùÌï¥ Ï£ºÏÑ∏Ïöî</Subtitle>
            <ModeSelectContainer>
              <Button onClick={() => handleModeSelect("Ï†ÑÏ≤¥ Í¥ÄÎûå")}>Ï†ÑÏ≤¥ Í¥ÄÎûå</Button>
              <Button onClick={() => handleModeSelect("ÎßûÏ∂§ Í¥ÄÎûå")}>ÎßûÏ∂§ Í¥ÄÎûå</Button>
            </ModeSelectContainer>
          </>
        )}

        {step === "list" && (
          <ArtworkList onSelect={handleSelect} floor={floor} />
        )}

        {step === "detail" && (
          <ArtworkDetail
            artwork={selectedArtwork}
            onDone={handleDone}
            detected={detected}    // üîπ arrived ÎåÄÏã† detectedÎ°ú Ï†úÏñ¥
          />
        )}
      </Layout>
    </AppContainer>
  );
}

export default App;

// (Ïä§ÌÉÄÏùºÏùÄ Í∏∞Ï°¥ Í∑∏ÎåÄÎ°ú)

const AppContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background-color: #f8f8f8;
  display: flex;
  justify-content: center;
  align-items: flex-start;
  padding-top: 15vh;
`;

const Layout = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
`;

const Title = styled.h1`
  font-size: 5rem;
  color: #222;
  margin-bottom: 8vh; 
  letter-spacing: 0.1rem; 
`;

const Subtitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 6vh;
`;

const ModeSelectContainer = styled.div`
  display: flex;
  gap: 10rem;
  margin-top: 3vh; 

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
  }
