import React, { useState, useCallback } from "react";
import styled from "styled-components";
import ArtworkList from "./components/ArtworkList";
import ArtworkDetail from "./components/ArtworkDetail";
import GlobalStyle from "./styles/GlobalStyle";
import useSocket from "./hooks/useSocket";

function App() {
  const [step, setStep] = useState("mode");
  const [selectedArtwork, setSelectedArtwork] = useState(null);
  const [isArrived, setIsArrived] = useState(false);

  // WebSocket 메시지 수신 핸들러
  const handleMessage = useCallback((data) => {
    console.log("💬 WebSocket 메시지:", data);
    if (data.command === "arrived") {
      setIsArrived(true);
    }
  }, []);

  const { send } = useSocket(handleMessage);

  const handleModeSelect = (mode) => {
    if (mode === "맞춤 관람") {
      setStep("list");
    } else {
      alert("전체 관람 모드는 현재 준비 중입니다.");
    }
  };

  const handleSelect = (art) => {
    setSelectedArtwork(art);
    setIsArrived(false);  // 새 작품 선택 시 초기화
    setStep("detail");

    // 이동 명령 전송
    send({
      command: "go_to",
      artwork: art.name,
      x: art.x,
      y: art.y,
      z: art.z
    });
  };

  const handleBackToList = () => {
    setStep("list");
    setSelectedArtwork(null);
    setIsArrived(false);
  };

  return (
    <AppContainer>
      <GlobalStyle />
      <Layout>
        <Title>ORBIT</Title>

        {step === "mode" && (
          <>
            <Subtitle>관람 방법을 선택해 주세요</Subtitle>
            <ModeSelectContainer>
              <Button onClick={() => handleModeSelect("전체 관람")}>전체 관람</Button>
              <Button onClick={() => handleModeSelect("맞춤 관람")}>맞춤 관람</Button>
            </ModeSelectContainer>
          </>
        )}

        {step === "list" && <ArtworkList onSelect={handleSelect} />}

        {step === "detail" && selectedArtwork && (
          isArrived ? (
            <>
              <ArtworkDetail artwork={selectedArtwork} />
              <BackButton onClick={handleBackToList}>다른 작품 보기</BackButton>
            </>
          ) : (
            <StatusText>로봇이 작품 앞으로 이동 중입니다...</StatusText>
          )
        )}
      </Layout>
    </AppContainer>
  );
}

export default App;


const AppContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background-color: #f8f8f8;
  display: flex;
  justify-content: center;
  align-items: flex-start;
  padding-top: 15vh;
`;

const Layout = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
`;

const Title = styled.h1`
  font-size: 5rem;
  color: #222;
  margin-bottom: 8vh; 
  letter-spacing: 0.1rem; 
`;

const Subtitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 6vh;
`;

const ModeSelectContainer = styled.div`
  display: flex;
  gap: 10rem;
  margin-top: 3vh; 

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
  }
`;

const Button = styled.button`
  font-size: 2rem;
  padding: 2rem 3.5rem;  
  border-radius: 16px;   
  border: none;
  background-color: #333;
  color: white;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #555;
  }

  &:active {
    background-color: #111;
  }
`;
