import React, { useEffect, useState, useCallback } from "react";
import useSocket from "../hooks/useSocket";
import styled from "styled-components";

function ArtworkDetail({ artwork, onDone }) {
  const [isMoving, setIsMoving] = useState(true);
  const [showInfo, setShowInfo] = useState(false);

  const handleMessage = useCallback((data) => {
    console.log("서버 응답:", data);
    // 향후 이동 완료 응답 처리 가능
  }, []);

  const { send } = useSocket(handleMessage);

  useEffect(() => {
    // 이동 명령 전송
    send({
      command: "go_to",
      artwork: artwork.name,
      x: artwork.x,
      y: artwork.y,
      z: artwork.z
    });

    // 시뮬레이션: 5초 후 이동 완료 → 4초 동안 설명 후 onDone
    setTimeout(() => {
      setIsMoving(false);
      setShowInfo(true);

      setTimeout(() => {
        setShowInfo(false);
        onDone();
      }, 4000); // 설명 시간
    }, 5000); // 이동 시간
  }, [artwork, send, onDone]);

  return (
    <Container>
      {isMoving ? (
        <StatusText>로봇이 작품 앞으로 이동 중입니다...</StatusText>
      ) : showInfo ? (
        <>
          <Image
            src={require(`../images/picture${artwork.id}.jpg`)}
            alt={artwork.name}
          />
          <Title>{artwork.name}</Title>
          <Author>{artwork.author}</Author>
          <Description>{artwork.description}</Description>
        </>
      ) : (
        <StatusText>설명이 끝났습니다. 다음 작품으로 넘어갑니다.</StatusText>
      )}
    </Container>
  );
}

export default ArtworkDetail;

// ✅ Styled Components
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem;
`;

const StatusText = styled.p`
  font-size: 1.8rem;
  margin-top: 4rem;
  color: #333;
`;

const Image = styled.img`
  width: 260px;
  height: 340px;
  object-fit: cover;
  border-radius: 12px;
  margin-bottom: 1.5rem;
  box-shadow: 0 6px 12px rgba(0,0,0,0.2);
`;

const Title = styled.h2`
  font-size: 2rem;
  margin: 0.5rem 0;
`;

const Author = styled.p`
  font-size: 1.2rem;
  color: #666;
  margin-bottom: 1rem;
`;

const Description = styled.p`
  font-size: 1.1rem;
  text-align: center;
  max-width: 500px;
  line-height: 1.6;
  color: #444;
`;
