import React from "react";
import styled from "styled-components";
import artworks from "../data/artworks";
import GlobalStyle from "../styles/GlobalStyle";

function ArtworkList({ onSelect }) {
  return (
    <ListContainer>
        <GlobalStyle />
            <Title>작품을 선택해주세요</Title>
            <CardList>
                {artworks.map((art, index) => (
                <Card key={art.id} onClick={() => onSelect(art)}>
                    <Image src={require(`../images/picture${index + 1}.jpg`)} alt={art.name} />
                    <ArtTitle>{art.name}</ArtTitle>
                </Card>
                ))}
            </CardList>
        </ListContainer>
    );
}

export default ArtworkList;

const ListContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background-color: #f8f8f8;
  display: flex;
  justify-content: center;
  align-items: flex-start;
  padding-top: 15vh;
`;

const Title = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 4vh;
`;

const CardList = styled.ul`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 3rem;
  list-style: none;
  padding: 0;
  margin: 0;

  /* ✅ 한 줄에 최대 4개까지만 보이도록 max-width 지정 */
  max-width: calc((280px + 3rem) * 4); /* 카드 너비 + gap × 4개 */
`;


const Card = styled.li`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 280px;
  cursor: pointer;
  background-color: #fff;
  border-radius: 16px;
  box-shadow: 0 6px 12px rgba(0,0,0,0.1);
  padding: 1rem;
  transition: transform 0.2s ease;

  &:hover {
    transform: scale(1.03);
  }
`;

const Image = styled.img`
  width: 200px;
  height: 250px;
  object-fit: cover;
  border-radius: 12px;
  margin-bottom: 1rem;
`;

const ArtTitle = styled.p`
  font-size: 1.5rem;
  font-weight: bold;
  text-align: center;
`;
