import React, { useState, useEffect, useRef, useCallback } from "react";
import styled from "styled-components";
import ArtworkList from "./components/ArtworkList";
import ArtworkDetail from "./components/ArtworkDetail";
import GlobalStyle from "./styles/GlobalStyle";
import client from "./mqttClient";

function App() {
  const [step, setStep] = useState("mode");
  const [selectedArtwork, setSelectedArtwork] = useState(null);
  const [floor, setFloor] = useState(1);

  // ÏÉÅÏÑ∏ ÌôîÎ©¥ ÏßÑÏûÖ Ï°∞Í±¥(AND)
  const [detected, setDetected] = useState(false);
  const [artworkDetectedFlag, setArtworkDetectedFlag] = useState(false);   // /robot2/artwork_detected
  const [arrivedPaintingFlag, setArrivedPaintingFlag] = useState(false);   // /robot2/arrived_painting

  // Î≥¥Ï°∞ ÏÉÅÌÉú
  const [arrived, setArrived] = useState(false); // ÏùºÎ∞ò ÎèÑÏ∞© ÌëúÏãúÏö©(ÏòµÏÖò)
  const [gohomeSent, setGohomeSent] = useState(false);

  // ÎèôÏãúÏ°∞Í±¥Ïö© ÌîåÎûòÍ∑∏: /robot2/person_detected, /robot3/arrived
  const [personDetected, setPersonDetected] = useState(false);
  const [robot3ArrivedFlag, setRobot3ArrivedFlag] = useState(false);

  // ÏµúÏã†Í∞íÏùÑ ÏúÑÌï¥ ref ÎèôÍ∏∞Ìôî
  const selectedRef = useRef(null);
  useEffect(() => { selectedRef.current = selectedArtwork; }, [selectedArtwork]);

  const personDetectedRef = useRef(false);
  useEffect(() => { personDetectedRef.current = personDetected; }, [personDetected]);

  const robot3ArrivedRef = useRef(false);
  useEffect(() => { robot3ArrivedRef.current = robot3ArrivedFlag; }, [robot3ArrivedFlag]);

  const gohomeSentRef = useRef(false);
  useEffect(() => { gohomeSentRef.current = gohomeSent; }, [gohomeSent]);

  // MQTT payload ‚Üí boolean ÌååÏÑú
  const parseBoolPayload = (payload) => {
    if (payload && typeof payload.length === "number") {
      if (payload.length === 1 && (payload[0] === 0 || payload[0] === 1)) {
        return payload[0] === 1;
      }
    }
    try {
      const txt = new TextDecoder().decode(payload).trim().toLowerCase();
      if (txt === "true" || txt === "1") return true;
      if (txt === "false" || txt === "0") return false;
      const obj = JSON.parse(txt);
      if (typeof obj === "boolean") return obj;
      if (obj && typeof obj.detected === "boolean") return obj.detected;
      if (obj && typeof obj.arrived === "boolean") return obj.arrived;
    } catch (_) {}
    return false;
  };

  // Îëê Ïã†Ìò∏Í∞Ä Î™®Îëê trueÏùº ÎïåÎßå ÏÉÅÏÑ∏ Î≥∏Î¨∏ ÌëúÏãú/Ï†ÑÌôò
  const tryEnterDetail = useCallback(() => {
    if (selectedRef.current && artworkDetectedFlag && arrivedPaintingFlag) {
      setDetected(true);
      setStep((prev) => (prev === "detail" ? prev : "detail"));
    }
  }, [artworkDetectedFlag, arrivedPaintingFlag]);

  // /robot3/gohome: /robot2/person_detected && /robot3/arrived ÎèôÏãúÏóê trueÏùº Îïå Ìïú Î≤àÎßå
  const trySendGohome = useCallback(() => {
    const now = Date.now();
  const bothTrue = personDetectedRef.current && robot3ArrivedRef.current;
  const closeEnough =
    Math.abs(personTsRef.current - arrived3TsRef.current) <= WINDOW_MS;

  if (bothTrue && closeEnough && !gohomeSentRef.current) {
    const one = Uint8Array.from([1]);
    client.publish("/robot3/gohome", one);
    console.log("üì§ /robot3/gohome ‚Üí <0x01> (strict window match)");
    setGohomeSent(true);
  }
}, []);

/// ÏàòÏã†Î∂ÄÏóêÏÑú ÌÉÄÏûÑÏä§ÌÉ¨ÌîÑ Í∏∞Î°ù ÌõÑ ÏãúÎèÑ
if (topic === "/robot3/arrived") {
  const isArrived = parseBoolPayload(payload);
  setRobot3ArrivedFlag(isArrived);
  if (isArrived) arrived3TsRef.current = Date.now();
  trySendGohomeStrict();
}

if (topic === "/robot2/person_detected") {
  const v = parseBoolPayload(payload);
  setPersonDetected(v);
  if (v) personTsRef.current = Date.now();
  trySendGohomeStrict();
}

  // Íµ¨ÎèÖ
  useEffect(() => {
    client.subscribe("/robot2/arrived", (err) =>
      err ? console.error("‚ùå /robot2/arrived Íµ¨ÎèÖ Ïã§Ìå®", err)
          : console.log("üì° /robot2/arrived Íµ¨ÎèÖ ÏÑ±Í≥µ")
    );
    client.subscribe("/robot3/arrived", (err) =>
      err ? console.error("‚ùå /robot3/arrived Íµ¨ÎèÖ Ïã§Ìå®", err)
          : console.log("üì° /robot3/arrived Íµ¨ÎèÖ ÏÑ±Í≥µ")
    );
    client.subscribe("/robot2/artwork_detected", (err) =>
      err ? console.error("‚ùå /robot2/artwork_detected Íµ¨ÎèÖ Ïã§Ìå®", err)
          : console.log("üì° /robot2/artwork_detected Íµ¨ÎèÖ ÏÑ±Í≥µ")
    );
    client.subscribe("/robot2/arrived_painting", (err) =>
      err ? console.error("‚ùå /robot2/arrived_painting Íµ¨ÎèÖ Ïã§Ìå®", err)
          : console.log("üì° /robot2/arrived_painting Íµ¨ÎèÖ ÏÑ±Í≥µ")
    );
    client.subscribe("/robot2/person_detected", (err) =>
      err ? console.error("‚ùå /robot2/person_detected Íµ¨ÎèÖ Ïã§Ìå®", err)
          : console.log("üì° /robot2/person_detected Íµ¨ÎèÖ ÏÑ±Í≥µ")
    );

    const onMessage = (topic, payload) => {
      // (Î≥¥Ï°∞) ÎèÑÏ∞© Ïã†Ìò∏
      if (topic === "/robot2/arrived") {
        const isArrived = parseBoolPayload(payload);
        console.log(`üì• /robot2/arrived =`, isArrived);
        setArrived(isArrived);
        return;
      }

      if (topic === "/robot3/arrived") {
        const isArrived = parseBoolPayload(payload);
        console.log(`üì• /robot3/arrived =`, isArrived);
        setArrived(isArrived);
        setRobot3ArrivedFlag(isArrived);
        // Ï°∞Í±¥ Ï∂©Ï°± Ïãú gohome ÏãúÎèÑ
        trySendGohome();
        return;
      }

      // ÏÉÅÏÑ∏ ÏßÑÏûÖ AND Ï°∞Í±¥Ïùò Ï≤´ Î≤àÏß∏ Ïã†Ìò∏
      if (topic === "/robot2/artwork_detected") {
        const ok = parseBoolPayload(payload);
        console.log("üì• /robot2/artwork_detected =", ok);
        setArtworkDetectedFlag(!!ok);
        if (!ok) setDetected(false); // Îëò Îã§ true ÎêòÍ∏∞ Ï†ÑÍπåÏßÄ 'Ïù¥Îèô Ï§ë' Ïú†ÏßÄ
        tryEnterDetail();
        return;
      }

      // ÏÉÅÏÑ∏ ÏßÑÏûÖ AND Ï°∞Í±¥Ïùò Îëê Î≤àÏß∏ Ïã†Ìò∏
      if (topic === "/robot2/arrived_painting") {
        const ok = parseBoolPayload(payload);
        console.log("üì• /robot2/arrived_painting =", ok);
        setArrivedPaintingFlag(!!ok);
        tryEnterDetail();
        return;
      }

      // ÎèôÏãú Ï°∞Í±¥ 1/2: ÏÇ¨Îûå Ïù∏Ïãù
      if (topic === "/robot2/person_detected") {
        const v = parseBoolPayload(payload);
        console.log("üì• /robot2/person_detected =", v);
        setPersonDetected(v);
        // Ï°∞Í±¥ Ï∂©Ï°± Ïãú gohome ÏãúÎèÑ
        trySendGohome();
        return;
      }
    };

    client.on("message", onMessage);
    return () => client.off("message", onMessage);
  }, [tryEnterDetail, trySendGohome]);

  const handleModeSelect = (mode) => {
    if (mode === "ÎßûÏ∂§ Í¥ÄÎûå") setStep("list");
    else alert("Ï†ÑÏ≤¥ Í¥ÄÎûå Î™®ÎìúÎäî ÌòÑÏû¨ Ï§ÄÎπÑ Ï§ëÏûÖÎãàÎã§.");
  };

  // Ï∏µ Î≥ÄÍ≤Ω ÌîåÎûòÍ∑∏ Î∞úÌñâ
  const publishFloorChangedFlag = (changed) => {
    const buf = Uint8Array.from([changed ? 1 : 0]);
    client.publish("/floor_changed", buf);
    console.log("üì§ /floor_changed ‚Üí", changed ? "<0x01>" : "<0x00>");
  };

  const handleSelect = (art) => {
    // ÏÉà ÏÇ¨Ïù¥ÌÅ¥ Ï¥àÍ∏∞Ìôî
    setDetected(false);
    setArrived(false);
    setGohomeSent(false);
    setArtworkDetectedFlag(false);
    setArrivedPaintingFlag(false);
    setPersonDetected(false);
    setRobot3ArrivedFlag(false);

    if (art.floor !== floor) {
      if (window.confirm(`${art.floor}Ï∏µÏúºÎ°ú Ïù¥ÎèôÌï†ÍπåÏöî?`)) {
        publishFloorChangedFlag(true);
        setFloor(art.floor);

        const message = String(art.id);
        client.publish("/robot3/artwork_id", message);
        client.publish("/robot2/artwork_id", message);
        console.log("üì§ /robot3/artwork_id ‚Üí", message);
        console.log("üì§ /robot2/artwork_id ‚Üí", message);

        setSelectedArtwork(art);
        selectedRef.current = art;
        setStep("detail"); // Î®ºÏ†Ä 'Ïù¥Îèô Ï§ë' ÌôîÎ©¥
      }
    } else {
      const message = String(art.id);
      client.publish("/robot3/artwork_id", message);
      client.publish("/robot2/artwork_id", message);
      console.log("üì§ /robot3/artwork_id ‚Üí", message);
      console.log("üì§ /robot2/artwork_id ‚Üí", message);

      setSelectedArtwork(art);
      selectedRef.current = art;
      setStep("detail");
    }
  };

  const handleDone = () => setStep("more"); // ÌïÑÏöî Ïãú "list" Îì±ÏúºÎ°ú

  return (
    <AppContainer>
      <GlobalStyle />
      <Layout>
        <Title>ORBIT</Title>

        {step === "mode" && (
          <>
            <Subtitle>Í¥ÄÎûå Î∞©Î≤ïÏùÑ ÏÑ†ÌÉùÌï¥ Ï£ºÏÑ∏Ïöî</Subtitle>
            <ModeSelectContainer>
              <Button onClick={() => handleModeSelect("Ï†ÑÏ≤¥ Í¥ÄÎûå")}>Ï†ÑÏ≤¥ Í¥ÄÎûå</Button>
              <Button onClick={() => handleModeSelect("ÎßûÏ∂§ Í¥ÄÎûå")}>ÎßûÏ∂§ Í¥ÄÎûå</Button>
            </ModeSelectContainer>
          </>
        )}

        {step === "list" && (
          <ArtworkList onSelect={handleSelect} floor={floor} />
        )}

        {step === "detail" && (
          <ArtworkDetail
            artwork={selectedArtwork}
            onDone={handleDone}
            detected={detected} // /robot2/artwork_detected && /robot2/arrived_painting Îëò Îã§ trueÏùº Îïå Î≥∏Î¨∏
          />
        )}
      </Layout>
    </AppContainer>
  );
}

