import React, { useState } from "react";
import styled from "styled-components";
import ArtworkList from "./components/ArtworkList";
import ArtworkDetail from "./components/ArtworkDetail";
import GlobalStyle from "./styles/GlobalStyle";
// :장대의_삼각_깃발: useState 등 모든 Hook은 반드시 함수 안에서 선언!
function App() {
  const [step, setStep] = useState("mode");
  const [selectedArtwork, setSelectedArtwork] = useState(null);
  const [floor, setFloor] = useState(1);      // <<== 여기에서 선언!
  // 모드 선택
  const handleModeSelect = (mode) => {
    if (mode === "맞춤 관람") {
      setStep("list");
    } else {
      alert("전체 관람 모드는 현재 준비 중입니다.");
    }
  };
  // 작품 선택: 층 다르면 "이동할까요?" 다이얼로그 띄우는 로직 등 넣을 수 있음
  const handleSelect = (art) => {
    if (art.floor !== floor) {
      // 층 다르면 다이얼로그 띄우는 추가 로직 (필요시)
      if (window.confirm(`${art.floor}층으로 이동할까요?`)) {
        setFloor(art.floor);           // 층 이동
        setSelectedArtwork(art);       // 상세로 이동
        setStep("detail");
      }
      // 아니요 누르면 아무 일도 안함
    } else {
      setSelectedArtwork(art);
      setStep("detail");
    }
  };
  const handleDone = () => {
    setStep("more");
  };
  return (
    <AppContainer>
      <GlobalStyle />
      <Layout>
        <Title>ORBIT</Title>
        {step === "mode" && (
          <>
            <Subtitle>관람 방법을 선택해 주세요</Subtitle>
            <ModeSelectContainer>
              <Button onClick={() => handleModeSelect("전체 관람")}>전체 관람</Button>
              <Button onClick={() => handleModeSelect("맞춤 관람")}>맞춤 관람</Button>
            </ModeSelectContainer>
          </>
        )}
        {step === "list" && <ArtworkList onSelect={handleSelect} floor={floor} />}
        {step === "detail" && <ArtworkDetail artwork={selectedArtwork} onDone={handleDone} />}
      </Layout>
    </AppContainer>
  );
}
export default App;
// Styled Components 이하 동일...
const AppContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background-color: #F8F8F8;
  display: flex;
  justify-content: center;
  align-items: flex-start;
  padding-top: 15vh;
`;
const Layout = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
`;
const Title = styled.h1`
  font-size: 5rem;
  color: #222;
  margin-bottom: 8vh;
  letter-spacing: 0.1rem;
`;
const Subtitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 6vh;
`;
const ModeSelectContainer = styled.div`
  display: flex;
  gap: 10rem;
  margin-top: 3vh;
  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
  }
`;
const Button = styled.button`
  font-size: 2rem;
  padding: 2rem 3.5rem;
  border-radius: 16px;
  border: none;
  background-color: #333;
  color: white;
  cursor: pointer;
  transition: background-color 0.3s;
  &:hover {
    background-color: #555;
  }
  &:active {
    background-color: #111;
  }
`;
