import React, { useEffect, useState } from "react";
import styled from "styled-components";
import ArtworkList from "./components/ArtworkList";
import ArtworkDetail from "./components/ArtworkDetail";
import GlobalStyle from "./styles/GlobalStyle";
import client from "./mqttClient"; // ✅ MQTT 클라이언트

import artworks from "./data/artworks"; // ✅ id → artwork 매핑용

function App() {
  const [step, setStep] = useState("mode");
  const [selectedArtwork, setSelectedArtwork] = useState(null);

  const handleModeSelect = (mode) => {
    if (mode === "맞춤 관람") {
      setStep("list");
    } else {
      alert("전체 관람 모드는 현재 준비 중입니다.");
    }
  };

  // ✅ 작품 클릭 시 MQTT로 ID 발행
  const handleSelect = (art) => {
    const message = JSON.stringify({ id: art.id });
    client.publish("/request/artwork", message);
    console.log("📤 MQTT 발행: /request/artwork →", message);
  };

  const handleDone = () => {
    setStep("more");
  };

  // ✅ MQTT 수신 처리
  useEffect(() => {
    client.on("connect", () => {
      console.log("✅ MQTT 연결됨");
      client.subscribe("/status/robot2", (err) => {
        if (err) console.error("❌ 구독 실패:", err);
        else console.log("📡 구독 완료: /status/robot2");
      });
    });

    client.on("message", (topic, payload) => {
      if (topic === "/status/robot2") {
        try {
          const data = JSON.parse(payload.toString());
          console.log("📥 MQTT 수신: ", data);

          if (data.status === "arrived") {
            const artwork = artworks.find((a) => a.id === data.id);
            if (artwork) {
              setSelectedArtwork(artwork);
              setStep("detail");
            }
          }
        } catch (e) {
          console.error("❌ 메시지 파싱 실패:", e);
        }
      }
    });
  }, []);

  return (
    <AppContainer>
      <GlobalStyle />
      <Layout>
        <Title>ORBIT</Title>
        {step === "mode" && (
          <>
            <Subtitle>관람 방법을 선택해 주세요</Subtitle>
            <ModeSelectContainer>
              <Button onClick={() => handleModeSelect("전체 관람")}>전체 관람</Button>
              <Button onClick={() => handleModeSelect("맞춤 관람")}>맞춤 관람</Button>
            </ModeSelectContainer>
          </>
        )}
        {step === "list" && <ArtworkList onSelect={handleSelect} />}
        {step === "detail" && selectedArtwork && (
          <ArtworkDetail artwork={selectedArtwork} onDone={handleDone} />
        )}
      </Layout>
    </AppContainer>
  );
}

export default App;

const AppContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background-color: #f8f8f8;
  display: flex;
  justify-content: center;
  align-items: flex-start;
  padding-top: 15vh;
`;

const Layout = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
`;

const Title = styled.h1`
  font-size: 5rem;
  color: #222;
  margin-bottom: 8vh; 
  letter-spacing: 0.1rem; 
`;

const Subtitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 6vh;
`;

const ModeSelectContainer = styled.div`
  display: flex;
  gap: 10rem;
  margin-top: 3vh; 

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
  }
`;

const Button = styled.button`
  font-size: 2rem;
  padding: 2rem 3.5rem;  
  border-radius: 16px;   
  border: none;
  background-color: #333;
  color: white;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #555;
  }

  &:active {
    background-color: #111;
  }
`;
