import React, { useEffect, useState, useCallback } from "react";
import useSocket from "../hooks/useSocket";

function ArtworkDetail({ artwork, onDone }) {
  const [status, setStatus] = useState("이동 중...");

  // WebSocket 메시지 수신 처리 (필요 시 서버 응답 기반으로 상태 처리)
  const handleMessage = useCallback((data) => {
    console.log("서버 응답:", data);
    // 필요한 경우 여기서 setStatus나 로직 조정 가능
  }, []);

  const { send } = useSocket(handleMessage);

  useEffect(() => {
    // WebSocket으로 좌표 전송
    send({
      command: "go_to",
      artwork: artwork.name,
      x: artwork.x,
      y: artwork.y,
      z: artwork.z
    });

    // 이동 + 감지 + 설명 시뮬레이션
    setTimeout(() => {
      setStatus("작품 감지 완료! 설명 중...");
      setTimeout(() => {
        setStatus("설명이 끝났습니다.");
        onDone();  // 다음 단계로 이동
      }, 4000); // 설명 시간
    }, 5000); // 이동 및 인식 시간
  }, [artwork, send, onDone]);

  return (
    <div>
      <h2>{artwork.name}</h2>
      <p>{status}</p>
    </div>
  );
}

export default ArtworkDetail;
