import React from "react";
import styled from "styled-components";

function ArtworkDetail({ artwork, detected, onExploreMore, onReject }) {
  if (!artwork) return null;

  return (
    <Container>
      {!detected ? (
        <StatusText>Î°úÎ¥áÏù¥ ÏûëÌíà ÏúÑÏπòÎ°ú Ïù¥ÎèôÌïòÍ≥† ÏûàÏñ¥Ïöî</StatusText>
      ) : (
        <Content>
          <ImageWrap>
            <Image
              src={require(`../images/picture${artwork.id}.jpg`)}
              alt={artwork.name}
            />
          </ImageWrap>

          <InfoWrap>
            <Title>{artwork.name}</Title>
            <Author>{artwork.author}</Author>
            <Description>{artwork.description}</Description>

            {/* Ïòà / ÏïÑÎãàÏò§ Î≤ÑÌäº */}
            <ButtonRow>
              <DecisionButton
                onClick={() => {
                  console.log("üü¢ [UI] Ïòà(Îçî ÎëòÎü¨Î≥¥Í∏∞) ÌÅ¥Î¶≠");
                  onExploreMore && onExploreMore();
                }}
              >
                Ïòà
              </DecisionButton>

              <DecisionButton
                onClick={() => {
                  console.log("üî¥ [UI] ÏïÑÎãàÏò§(Í±∞Ï†à) ÌÅ¥Î¶≠");
                  onReject && onReject();
                }}
              >
                ÏïÑÎãàÏò§
              </DecisionButton>
            </ButtonRow>
          </InfoWrap>
        </Content>
      )}
    </Container>
  );
}

export default ArtworkDetail;

// ===== Styles =====
const Container = styled.div`
  width: 100%;
  padding: 2rem;
`;

const StatusText = styled.p`
  font-size: 1.8rem;
  margin-top: 4rem;
  color: #333;
  text-align: center;
`;

const Content = styled.div`
  display: flex;
  gap: 2rem;
  align-items: flex-start;
  justify-content: center;
  max-width: 960px;
  margin: 0 auto;
`;

const ImageWrap = styled.div`
  flex: 0 0 300px;
`;

const Image = styled.img`
  width: 100%;
  height: auto;
  max-height: 520px;
  object-fit: cover;
  border-radius: 12px;
  box-shadow: 0 6px 12px rgba(0,0,0,0.2);
`;

const InfoWrap = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const Title = styled.h2`
  font-size: 2rem;
  margin: 0.5rem 0 0.25rem 0;
`;

const Author = styled.p`
  font-size: 1.2rem;
  color: #666;
  margin: 0 0 0.5rem 0;
`;

const Description = styled.p`
  font-size: 1.05rem;
  line-height: 1.7;
  color: #444;
  white-space: pre-wrap;
`;

const MoreButton = styled.button`
  margin-top: 2rem;
  align-self: flex-start;
  font-size: 1.2rem;
  padding: 0.9rem 1.6rem;
  background-color: #444;
  color: white;
  border: none;
  border-radius: 10px;
  cursor: pointer;
  &:hover { background-color: #666; }
`;
