import React, { useState, useEffect } from "react";
import styled from "styled-components";
import ArtworkList from "./components/ArtworkList";
import ArtworkDetail from "./components/ArtworkDetail";
import GlobalStyle from "./styles/GlobalStyle";
import client from "./mqttClient";

function App() {
  const [step, setStep] = useState("mode");
  const [selectedArtwork, setSelectedArtwork] = useState(null);
  const [floor, setFloor] = useState(1);          // ÌòÑÏû¨ ÏÇ¨ÎûåÏù¥ ÏûàÎäî Ï∏µ (Ï¥àÍ∏∞: 1Ï∏µ)
  const [arrived, setArrived] = useState(false);  // Î°úÎ¥á ÎèÑÏ∞© Ïó¨Î∂Ä

  // ‚úÖ MQTT payload ‚Üí boolean ÌååÏÑú (1Î∞îÏù¥Ìä∏ 0/1, Î¨∏ÏûêÏó¥ "true"/"false", JSONÍπåÏßÄ ÎåÄÏùë)
  const parseBoolPayload = (payload) => {
    // Î∏åÎùºÏö∞Ï†Ä mqtt.js: Î≥¥ÌÜµ Uint8Array(1Î∞îÏù¥Ìä∏ 0/1) ÎòêÎäî Î¨∏ÏûêÏó¥
    if (payload && typeof payload.length === "number") {
      if (payload.length === 1 && (payload[0] === 0 || payload[0] === 1)) {
        return payload[0] === 1;
      }
    }
    try {
      const txt = new TextDecoder().decode(payload).trim().toLowerCase();
      if (txt === "true" || txt === "1") return true;
      if (txt === "false" || txt === "0") return false;
      // JSON ÎåÄÎπÑ: {"arrived":true}
      const obj = JSON.parse(txt);
      if (typeof obj === "boolean") return obj;
      if (obj && typeof obj.arrived === "boolean") return obj.arrived;
    } catch (_) {}
    return false;
  };

  // ‚úÖ /robot3/arrived Íµ¨ÎèÖ Î∞è ÏàòÏã† Ï≤òÎ¶¨
  useEffect(() => {
    // Ïó∞Í≤∞ ÌõÖÏóêÏÑú Ïù¥ÎØ∏ Íµ¨ÎèÖÌñàÎã§Î©¥ ÏÉùÎûµ Í∞ÄÎä•ÌïòÏßÄÎßå, ÏïàÏ†ÑÌïòÍ≤å Ìïú Î≤à Îçî ÏãúÎèÑ
    client.subscribe("/robot3/arrived", (err) => {
      if (err) console.error("‚ùå /robot3/arrived Íµ¨ÎèÖ Ïã§Ìå®", err);
      else console.log("üì° /robot3/arrived Íµ¨ÎèÖ ÏÑ±Í≥µ");
    });

    const onMessage = (topic, payload) => {
      if (topic !== "/robot3/arrived") return;
      const isArrived = parseBoolPayload(payload);
      console.log("üì• ÏàòÏã†: /robot3/arrived =", isArrived);
      setArrived(isArrived);
      // ÌïÑÏöî Ïãú ÎèÑÏ∞© Ï¶âÏãú ÌôîÎ©¥ Ï†ÑÌôò/ÏïåÎ¶º Ï≤òÎ¶¨ Í∞ÄÎä•
      // if (isArrived) setStep("detail");
    };

    client.on("message", onMessage);
    return () => client.off("message", onMessage);
  }, []);

  const handleModeSelect = (mode) => {
    if (mode === "ÎßûÏ∂§ Í¥ÄÎûå") setStep("list");
    else alert("Ï†ÑÏ≤¥ Í¥ÄÎûå Î™®ÎìúÎäî ÌòÑÏû¨ Ï§ÄÎπÑ Ï§ëÏûÖÎãàÎã§.");
  };

  // ‚úÖ Ï∏µ Î≥ÄÍ≤ΩÏãú /floor_changed = 1Î∞îÏù¥Ìä∏ Î∂àÎ¶∞(0x01/0x00) Î∞úÌñâ
  const publishFloorChangedFlag = (changed) => {
    const buf = Uint8Array.from([changed ? 1 : 0]); // 1=true, 0=false
    client.publish("/floor_changed", buf);
    console.log("üì§ MQTT Î∞úÌñâ: /floor_changed ‚Üí", changed ? "<0x01>" : "<0x00>");
  };

  const handleSelect = (art) => {
    // ÏÉà Ïù¥Îèô ÏãúÏûë Ïãú Ïù¥Ï†Ñ ÎèÑÏ∞© ÏÉÅÌÉú Ï¥àÍ∏∞Ìôî(ÏÑ†ÌÉù)
    setArrived(false);

    // ÏûëÌíàÏù¥ ÏûàÎäî Ï∏µÍ≥º ÌòÑÏû¨ ÏÇ¨ÎûåÏù¥ ÏûàÎäî Ï∏µ ÎπÑÍµê
    if (art.floor !== floor) {
      if (window.confirm(`${art.floor}Ï∏µÏúºÎ°ú Ïù¥ÎèôÌï†ÍπåÏöî?`)) {
        // ‚úÖ Ï∏µ Î≥ÄÍ≤Ω Ïù¥Î≤§Ìä∏ Î®ºÏ†Ä Î∞úÌñâ
        publishFloorChangedFlag(true);

        // Ï∏µ ÏÉÅÌÉú Í∞±Ïã†
        setFloor(art.floor);

        // ÏûëÌíà IDÎ•º Î¨∏ÏûêÏó¥Î°ú Î∞úÌñâ (Ïòà: "1")
        const message = String(art.id);
        client.publish("/robot3/artwork_id", message);
        client.publish("/robot2/artwork_id", message);

        console.log("üì§ MQTT Î∞úÌñâ: /robot3/artwork_id ‚Üí", message);
        console.log("üì§ MQTT Î∞úÌñâ: /robot2/artwork_id ‚Üí", message);

        // ÏÉÅÏÑ∏ ÌôîÎ©¥ÏúºÎ°ú Ïù¥Îèô
        setSelectedArtwork(art);
        setStep("detail");
      }
    } else {
      // Í∞ôÏùÄ Ï∏µÏù¥Î©¥ Î∞îÎ°ú Î∞úÌñâ ÌõÑ ÏÉÅÏÑ∏Î°ú Ïù¥Îèô
      const message = String(art.id);
      client.publish("/robot3/artwork_id", message);
      client.publish("/robot2/artwork_id", message);
      console.log("üì§ MQTT Î∞úÌñâ: /robot3/artwork_id ‚Üí", message);
      console.log("üì§ MQTT Î∞úÌñâ: /robot3/artwork_id ‚Üí", message);

      setSelectedArtwork(art);
      setStep("detail");
    }
  };

  const handleDone = () => setStep("more");

  return (
    <AppContainer>
      <GlobalStyle />
      <Layout>
        <Title>ORBIT</Title>

        {step === "mode" && (
          <>
            <Subtitle>Í¥ÄÎûå Î∞©Î≤ïÏùÑ ÏÑ†ÌÉùÌï¥ Ï£ºÏÑ∏Ïöî</Subtitle>
            <ModeSelectContainer>
              <Button onClick={() => handleModeSelect("Ï†ÑÏ≤¥ Í¥ÄÎûå")}>Ï†ÑÏ≤¥ Í¥ÄÎûå</Button>
              <Button onClick={() => handleModeSelect("ÎßûÏ∂§ Í¥ÄÎûå")}>ÎßûÏ∂§ Í¥ÄÎûå</Button>
            </ModeSelectContainer>
          </>
        )}

        {step === "list" && <ArtworkList onSelect={handleSelect} floor={floor} />}

        {step === "detail" && (
          <ArtworkDetail
            artwork={selectedArtwork}
            onDone={handleDone}
            arrived={arrived}   // ‚úÖ ÌïÑÏöî Ïãú ÏÉÅÏÑ∏ ÌôîÎ©¥ÏóêÏÑú ÎèÑÏ∞© ÏÉÅÌÉú ÌôúÏö©
          />
        )}
      </Layout>
    </AppContainer>
  );
}

export default App;

// (Ïä§ÌÉÄÏùºÏùÄ Í∏∞Ï°¥ Í∑∏ÎåÄÎ°ú)

const AppContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background-color: #f8f8f8;
  display: flex;
  justify-content: center;
  align-items: flex-start;
  padding-top: 15vh;
`;

const Layout = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
`;

const Title = styled.h1`
  font-size: 5rem;
  color: #222;
  margin-bottom: 8vh; 
  letter-spacing: 0.1rem; 
`;

const Subtitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 6vh;
`;

const ModeSelectContainer = styled.div`
  display: flex;
  gap: 10rem;
  margin-top: 3vh; 

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
  }
`;

const Button = styled.button`
  font-size: 2rem;
  padding: 2rem 3.5rem;  
  border-radius: 16px;   
  border: none;
  background-color: #333;
  color: white;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #555;
  }

  &:active {
    background-color: #111;
  }
`;
