import React, { useState, useEffect } from "react";
import styled from "styled-components";
import ArtworkList from "./components/ArtworkList";
import ArtworkDetail from "./components/ArtworkDetail";
import GlobalStyle from "./styles/GlobalStyle";
import client from "./mqttClient";

function App() {
  const [step, setStep] = useState("mode");
  const [selectedArtwork, setSelectedArtwork] = useState(null);
  const [floor, setFloor] = useState(1);          // 현재 사람이 있는 층 (초기: 1층)
  const [arrived, setArrived] = useState(false);  // 로봇 도착 여부

  // ✅ MQTT payload → boolean 파서 (1바이트 0/1, 문자열 "true"/"false", JSON까지 대응)
  const parseBoolPayload = (payload) => {
    // 브라우저 mqtt.js: 보통 Uint8Array(1바이트 0/1) 또는 문자열
    if (payload && typeof payload.length === "number") {
      if (payload.length === 1 && (payload[0] === 0 || payload[0] === 1)) {
        return payload[0] === 1;
      }
    }
    try {
      const txt = new TextDecoder().decode(payload).trim().toLowerCase();
      if (txt === "true" || txt === "1") return true;
      if (txt === "false" || txt === "0") return false;
      // JSON 대비: {"arrived":true}
      const obj = JSON.parse(txt);
      if (typeof obj === "boolean") return obj;
      if (obj && typeof obj.arrived === "boolean") return obj.arrived;
    } catch (_) {}
    return false;
  };

  // ✅ /robot3/arrived 구독 및 수신 처리
  useEffect(() => {
    // 연결 훅에서 이미 구독했다면 생략 가능하지만, 안전하게 한 번 더 시도
    client.subscribe("/robot3/arrived", (err) => {
      if (err) console.error("❌ /robot3/arrived 구독 실패", err);
      else console.log("📡 /robot3/arrived 구독 성공");
    });

    const onMessage = (topic, payload) => {
      if (topic !== "/robot3/arrived") return;
      const isArrived = parseBoolPayload(payload);
      console.log("📥 수신: /robot3/arrived =", isArrived);
      setArrived(isArrived);
      // 필요 시 도착 즉시 화면 전환/알림 처리 가능
      // if (isArrived) setStep("detail");
    };

    client.on("message", onMessage);
    return () => client.off("message", onMessage);
  }, []);

  const handleModeSelect = (mode) => {
    if (mode === "맞춤 관람") setStep("list");
    else alert("전체 관람 모드는 현재 준비 중입니다.");
  };

  // ✅ 층 변경시 /floor_changed = 1바이트 불린(0x01/0x00) 발행
  const publishFloorChangedFlag = (changed) => {
    const buf = Uint8Array.from([changed ? 1 : 0]); // 1=true, 0=false
    client.publish("/floor_changed", buf);
    console.log("📤 MQTT 발행: /floor_changed →", changed ? "<0x01>" : "<0x00>");
  };

  const handleSelect = (art) => {
    // 새 이동 시작 시 이전 도착 상태 초기화(선택)
    setArrived(false);

    // 작품이 있는 층과 현재 사람이 있는 층 비교
    if (art.floor !== floor) {
      if (window.confirm(`${art.floor}층으로 이동할까요?`)) {
        // ✅ 층 변경 이벤트 먼저 발행
        publishFloorChangedFlag(true);

        // 층 상태 갱신
        setFloor(art.floor);

        // 작품 ID를 문자열로 발행 (예: "1")
        const message = String(art.id);
        client.publish("/robot3/artwork_id", message);
        client.publish("/robot2/artwork_id", message);

        console.log("📤 MQTT 발행: /robot3/artwork_id →", message);
        console.log("📤 MQTT 발행: /robot2/artwork_id →", message);

        // 상세 화면으로 이동
        setSelectedArtwork(art);
        setStep("detail");
      }
    } else {
      // 같은 층이면 바로 발행 후 상세로 이동
      const message = String(art.id);
      client.publish("/robot3/artwork_id", message);
      client.publish("/robot2/artwork_id", message);
      console.log("📤 MQTT 발행: /robot3/artwork_id →", message);
      console.log("📤 MQTT 발행: /robot3/artwork_id →", message);

      setSelectedArtwork(art);
      setStep("detail");
    }
  };

  const handleDone = () => setStep("more");

  return (
    <AppContainer>
      <GlobalStyle />
      <Layout>
        <Title>ORBIT</Title>

        {step === "mode" && (
          <>
            <Subtitle>관람 방법을 선택해 주세요</Subtitle>
            <ModeSelectContainer>
              <Button onClick={() => handleModeSelect("전체 관람")}>전체 관람</Button>
              <Button onClick={() => handleModeSelect("맞춤 관람")}>맞춤 관람</Button>
            </ModeSelectContainer>
          </>
        )}

        {step === "list" && <ArtworkList onSelect={handleSelect} floor={floor} />}

        {step === "detail" && (
          <ArtworkDetail
            artwork={selectedArtwork}
            onDone={handleDone}
            arrived={arrived}   // ✅ 필요 시 상세 화면에서 도착 상태 활용
          />
        )}
      </Layout>
    </AppContainer>
  );
}

export default App;

// (스타일은 기존 그대로)

const AppContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background-color: #f8f8f8;
  display: flex;
  justify-content: center;
  align-items: flex-start;
  padding-top: 15vh;
`;

const Layout = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
`;

const Title = styled.h1`
  font-size: 5rem;
  color: #222;
  margin-bottom: 8vh; 
  letter-spacing: 0.1rem; 
`;

const Subtitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 6vh;
`;

const ModeSelectContainer = styled.div`
  display: flex;
  gap: 10rem;
  margin-top: 3vh; 

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
  }
`;

const Button = styled.button`
  font-size: 2rem;
  padding: 2rem 3.5rem;  
  border-radius: 16px;   
  border: none;
  background-color: #333;
  color: white;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #555;
  }

  &:active {
    background-color: #111;
  }
`;
