import React, { useEffect, useState } from "react";
import styled from "styled-components";
import client from "../mqttClient"; // ‚úÖ MQTT ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏ Í∞ÄÏ†∏Ïò§Í∏∞

function ArtworkDetail({ artwork, onDone }) {
  const [hasArrived, setHasArrived] = useState(false);
  const [showMoreButton, setShowMoreButton] = useState(false);

  useEffect(() => {
    // ‚úÖ MQTT Î©îÏãúÏßÄ ÏàòÏã† Ìï∏Îì§Îü¨
    const handleMessage = (topic, message) => {
      if (topic === "/robot3/arrived") {
        const payload = message.toString();
        console.log("üì• MQTT Î©îÏãúÏßÄ ÏàòÏã†:", payload);

        if (payload === "true" || payload === "True") {
          setHasArrived(true);
          setTimeout(() => setShowMoreButton(true), 5000);
        }
      }
    };

    // ‚úÖ Î©îÏãúÏßÄ Ìï∏Îì§Îü¨ Îì±Î°ù
    client.on("message", handleMessage);

    // ‚úÖ MQTT Íµ¨ÎèÖ (ÌïÑÏöî Ïãú)
    client.subscribe("/robot3/arrived", (err) => {
      if (err) {
        console.error("‚ùå /robot3/arrived Íµ¨ÎèÖ Ïã§Ìå®:", err);
      } else {
        console.log("üì° /robot3/arrived Íµ¨ÎèÖ ÏÑ±Í≥µ");
      }
    });

    // ‚úÖ cleanup
    return () => {
      client.off("message", handleMessage);
    };
  }, []);

  useEffect(() => {
    // Î°úÎ¥áÏóêÍ≤å Ïù¥Îèô Î™ÖÎ†π MQTTÎ°ú Î≥¥ÎÇº ÏàòÎèÑ ÏûàÏùå
    // Ïòà: /request/artwork ‚Üí artwork.id Ï†ÑÏÜ°
    // ÎòêÎäî Îã§Î•∏ Î°úÏßÅÏúºÎ°ú Ïù¥ÎØ∏ Ïù¥Îèô Ï§ëÏù¥ÎùºÎ©¥ Ïù¥Í±¥ ÏÉùÎûµ
  }, [artwork]);

  return (
    <Container>
      {!hasArrived ? (
        <StatusText>Î°úÎ¥áÏù¥ ÏûëÌíà ÏúÑÏπòÎ°ú Ïù¥ÎèôÌïòÍ≥† ÏûàÏñ¥Ïöî</StatusText>
      ) : (
        <>
          <Image
            src={require(`../images/picture${artwork.id}.jpg`)}
            alt={artwork.name}
          />
          <Title>{artwork.name}</Title>
          <Author>{artwork.author}</Author>
          <Description>{artwork.description}</Description>

          {showMoreButton && (
            <MoreButton onClick={onDone}>Îçî ÎëòÎü¨Î≥¥ÏãúÍ≤†Ïñ¥Ïöî?</MoreButton>
          )}
        </>
      )}
    </Container>
  );
}

export default ArtworkDetail;

const Container = styled.div`
  display: flex;
  width: 100%;
  justify-content: center;
  padding: 2rem;
`;

const StatusText = styled.p`
  font-size: 1.8rem;
  margin-top: 4rem;
  color: #333;
`;

const ContentRow = styled.div`
  display: flex;
  gap: 2.5rem;
  align-items: flex-start;
  max-width: 900px;
  width: 100%;

  @media (max-width: 900px) {
    flex-direction: column;
    align-items: center;
  }
`;

const ImageWrap = styled.div`
  flex: 0 0 auto;
`;

const Image = styled.img`
  width: 320px;
  height: 420px;
  object-fit: cover;
  border-radius: 12px;
  box-shadow: 0 6px 12px rgba(0,0,0,0.2);

  @media (max-width: 900px) {
    width: 260px;
    height: 340px;
  }
`;

const Details = styled.div`
  flex: 1 1 auto;
  display: flex;
  flex-direction: column;
  gap: 0.8rem;
`;

const Title = styled.h2`
  font-size: 2rem;
  margin: 0.2rem 0 0.4rem 0;
`;

const Author = styled.p`
  font-size: 1.2rem;
  color: #666;
  margin-bottom: 0.8rem;
`;

const Description = styled.p`
  font-size: 1.05rem;
  line-height: 1.6;
  color: #444;
  white-space: pre-line; /* Ï§ÑÎ∞îÍøà Ïú†ÏßÄ(ÌïÑÏöîÏãú) */
`;

const MoreButton = styled.button`
  align-self: flex-start;
  margin-top: 1.6rem;
  font-size: 1.1rem;
  padding: 0.8rem 1.4rem;
  background-color: #444;
  color: white;
  border: none;
  border-radius: 10px;
  cursor: pointer;

  &:hover {
    background-color: #666;
  }
`;
